/* eslint-disable react-hooks/exhaustive-deps */
import axios from "axios";
import { useCallback, useState } from "react";

import { Participant } from "../types/api/Participant";
import { useMessage } from "./useMessage";

export const useAllParticipant = () => {
  const { showMessage } = useMessage();
  const [loading2, setLoading2] = useState(false);
  const [users1, setUsers1] = useState<Array<Participant>>([]);
  const [users2, setUsers2] = useState<Array<Participant>>([]);

  const getUsers = useCallback((tournamentId: number, classes: string[]) => {
    setLoading2(true);
    const [className1, className2] = classes;

    axios
      .get<{ [key: string]: Participant[] }>(`http://localhost:8080/apiTournament/getParticipant?tournamentId=${tournamentId}&className1=${encodeURIComponent(className1)}&className2=${encodeURIComponent(className2)}`) 
      .then((res) => {
        const usersArray1 = res.data[className1] || [];
        const usersArray2 = res.data[className2] || [];

        const combinedUsersArray = [...usersArray1, ...usersArray2];

        setUsers(combinedUsersArray);
      })
      .catch(() => {
        showMessage({ title: "参加者取得に失敗しました", status: "error" });
      })
      .finally(() => {
        setLoading2(false);
      });
  }, []);
  return { getUsers, loading2, users1 };
};
