import {
  Stack,
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalCloseButton,
  ModalBody,
  FormLabel,
  FormControl,
  ModalFooter,
  Select,
  Center,
  Spinner,
} from "@chakra-ui/react";
import React, { ChangeEvent, useEffect, useContext, useState } from "react";
import { memo } from "react";

import { tournament } from "../../../types/api/tournament";
import { Participant } from "../../../types/api/Participant";

type Props = {
  userId: number | undefined;
  isOpen: boolean;
  onClose: () => void;
  usersR1: Array<Participant>;
  usersR2: Array<Participant>;
  clases: Array<Participant>;
  selectedReTournament: tournament | null;
};

export const ResultDetailModal = memo((props: Props) => {
  const { isOpen, onClose, usersR1, usersR2, clases, selectedReTournament } = props;

  const [loading, setLoading] = useState(true);
  const [className1, className2] = clases;
  const [expected3, setExpected3] = useState<Array<Participant>>([]);
  const [expected4, setExpected4] = useState<Array<Participant>>([]);


  return (
    <Modal
      isOpen={isOpen}
      onClose={onClose}
      autoFocus={false}
      motionPreset="slideInBottom"
    >
      <ModalOverlay />
      <ModalContent pb={2}>
        <ModalHeader>予想</ModalHeader>
        <ModalCloseButton />
        <ModalBody mx={4}>
          {loading ? (
            <Center h="100vh">
              <Spinner />
            </Center>
          ) : usersR1.length === 0 && usersR2.length === 0 ? (
            <div>誰も予想していません。</div>
          ) : (
            <Stack spacing={4}>
              {usersR1.length !== 0 && (
                <>
                  <FormControl>
                    {className1 && className1.className && (
                      <FormLabel>{String(className1.className)}</FormLabel>
                    )}
                    <FormLabel>1位</FormLabel>

                  </FormControl>
                  <FormControl>
                    <FormLabel>2位</FormLabel>

                  </FormControl>
                  <FormControl>
                    <FormLabel>3位</FormLabel>

                  </FormControl>
                </>
              )}
              {usersR2.length !== 0 && (
                <>
                  <FormControl>
                    {className2 && className2.className && (
                      <FormLabel>{String(className2.className)}</FormLabel>
                    )}
                    <FormLabel>1位</FormLabel>

                  </FormControl>
                  <FormControl>
                    <FormLabel>2位</FormLabel>

                  </FormControl>
                  <FormControl>
                    <FormLabel>3位</FormLabel>

                  </FormControl>
                </>
              )}
            </Stack>
          )}
        </ModalBody>
      </ModalContent>
    </Modal>
  );
});
