import {
  Stack,
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalCloseButton,
  ModalBody,
  FormLabel,
  FormControl,
  Input,
  ModalFooter,
  Select,
  Text,
  Center,
} from "@chakra-ui/react";
import React, { ChangeEvent, useEffect, useState } from "react";
import { memo } from "react";

import { PrimaryButton } from "../../atoms/button/PrimaryButton";
import { tournament } from "../../../types/api/tournament";
import { Participant } from "../../../types/api/Participant";

type Props = {
  isOpen: boolean;
  onClose: () => void;
  users1: Array<Participant>;
  users2: Array<Participant>;
  clases: Array<Participant>;
  selectedTournament: tournament | null;
};

export const TournamentDetailModal = memo((props: Props) => {
  const { isOpen, onClose, users1, users2, clases, selectedTournament } = props;

  const [number1, setNumber1] = useState<Record<string, string>>({});
  const [number2, setNumber2] = useState("");
  const [className1, className2] = clases;


  const onChangeNumber1 = (e: React.ChangeEvent<HTMLSelectElement>, className: string) => {
    const selectedClassName = className1?.className || '';
    setNumber1({
      [selectedClassName]: e.target.value,
    });
  };
  const onChangeNumber2 = (e: ChangeEvent<HTMLInputElement>) =>
    setNumber2(e.target.value);

  const onClickUpdate = () => alert();

  return (
    <Modal
  isOpen={isOpen}
  onClose={onClose}
  autoFocus={false}
  motionPreset="slideInBottom"
>
  <ModalOverlay />
  <ModalContent pb={2}>
    <ModalHeader>予想</ModalHeader>
    <ModalCloseButton />
    <ModalBody mx={4}>
      <Stack spacing={4}>
        {users1.length > 0 ? (
          <>
            <FormControl>
              {className1 && className1.className && (
                <FormLabel>{String(className1.className)}</FormLabel>
              )}
              <FormLabel>1位</FormLabel>
              <Select
                onChange={(e) => onChangeNumber1(e, className1?.className)}
                value={number1[className1?.className] || ""}
              >
                <option value="" disabled hidden>
                  選択してください
                </option>
                {(users1 as Array<Participant>)
                  .filter((user) => user.className === className1?.className)
                  .map((filteredUser, participantId) => (
                    <option key={participantId} value={filteredUser.participantName}>
                      {`${filteredUser.participantName} - ${filteredUser.affiliation}`}
                    </option>
                  ))}
              </Select>
            </FormControl>
            <FormControl>
              <FormLabel>2位</FormLabel>
              <Input
                value={number2}
                onChange={onChangeNumber2}
              />
            </FormControl>
          </>
        ) : (
          <Text>参加者はいません</Text>
        )}
      </Stack>
    </ModalBody>
    <ModalFooter>
      <PrimaryButton onClick={onClickUpdate}>登録</PrimaryButton>
    </ModalFooter>
  </ModalContent>
</Modal>
  );
});
