import {
  Stack,
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalCloseButton,
  ModalBody,
  FormLabel,
  FormControl,
  Input,
  ModalFooter,
} from "@chakra-ui/react";
import React, { ChangeEvent, useEffect, useState } from "react";
import { memo } from "react";

import { PrimaryButton } from "../../atoms/button/PrimaryButton";

type Props = {
  isOpen: boolean;
  onClose: () => void;
};

export const TournamentDetailModal = memo((props: Props) => {
  const { isOpen, onClose } = props;

  const [name1, setName1] = useState("");
  const [name2, setName2] = useState("");


  const onChange1 = (e: ChangeEvent<HTMLInputElement>) =>
    setName1(e.target.value);
  const onChange2 = (e: ChangeEvent<HTMLInputElement>) =>
    setName2(e.target.value);

  const onClickUpdate = () => alert();

  return (
    <Modal
      isOpen={isOpen}
      onClose={onClose}
      autoFocus={false}
      motionPreset="slideInBottom"
    >
      <ModalOverlay />
      <ModalContent pb={2}>
        <ModalHeader>予想</ModalHeader>
        <ModalCloseButton />
        <ModalBody mx={4}>
          <Stack spacing={4}>
            <FormControl>
              <FormLabel>1位</FormLabel>
              <Input
                value={name1}
                onChange={onChange1}  
              />
            </FormControl>
            <FormControl>
              <FormLabel>2位</FormLabel>
              <Input
                value={name2}
                onChange={onChange2}
              />
            </FormControl>
          </Stack>
        </ModalBody>
          <ModalFooter>
            <PrimaryButton onClick={onClickUpdate}>登録</PrimaryButton>
          </ModalFooter>
      </ModalContent>
    </Modal>
  );
});
