import {
  Stack,
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalCloseButton,
  ModalBody,
  FormLabel,
  FormControl,
  ModalFooter,
  Select,
  Center,
  Spinner,
} from "@chakra-ui/react";
import React, { ChangeEvent, useEffect, useContext, useState } from "react";
import { memo } from "react";

import { PrimaryButton } from "../../atoms/button/PrimaryButton";
import { tournament } from "../../../types/api/tournament";
import { Participant } from "../../../types/api/Participant";
import { useInnsertExpected } from "../../../hooks/useInnsertExpected";

type Props = {
  userId: number | undefined;
  isOpen: boolean;
  onClose: () => void;
  usersR1: Array<Participant>;
  usersR2: Array<Participant>;
  users3: Array<Participant>;
  users4: Array<Participant>;
  clases: Array<Participant>;
  selectedTournament: tournament | null;
};

export const ResultDetailModal = memo((props: Props) => {
  const { isOpen, onClose, usersR1, usersR2, clases, selectedTournament } = props;

  const [loading, setLoading] = useState(true);
  const { innsertExpected, loading2 } = useInnsertExpected();
  const [className1, className2] = clases;
  const [expected3, setExpected3] = useState<Array<Participant>>([]);
  const [expected4, setExpected4] = useState<Array<Participant>>([]);


  useEffect(() => {
    // モーダルが表示された時点でローディングを開始
    setLoading(true);

    // 1秒後にローディングを終了
    const timer = setTimeout(() => {
      setLoading(false);
    }, 4000);

    return () => clearTimeout(timer);
  }, [isOpen]);  // isOpen が変更されたときに再実行  

  return (
    <Modal
      isOpen={isOpen}
      onClose={onClose}
      autoFocus={false}
      motionPreset="slideInBottom"
    >
      <ModalOverlay />
      <ModalContent pb={2}>
        <ModalHeader>予想</ModalHeader>
        <ModalCloseButton />
        <ModalBody mx={4}>
          {loading ? (
            <Center h="100vh">
              <Spinner />
            </Center>
          ) : users1.length === 0 && users2.length === 0 ? (
            <div>参加者がいない、又は不都合が発生しました</div>
          ) : (
            <Stack spacing={4}>
              {users1.length !== 0 && (
                <>
                  <FormControl>
                    {className1 && className1.className && (
                      <FormLabel>{String(className1.className)}</FormLabel>
                    )}
                    <FormLabel>1位</FormLabel>

                  </FormControl>
                  <FormControl>
                    <FormLabel>2位</FormLabel>

                  </FormControl>
                  <FormControl>
                    <FormLabel>3位</FormLabel>

                  </FormControl>
                </>
              )}
              {users2.length !== 0 && (
                <>
                  <FormControl>
                    {className2 && className2.className && (
                      <FormLabel>{String(className2.className)}</FormLabel>
                    )}
                    <FormLabel>1位</FormLabel>

                  </FormControl>
                  <FormControl>
                    <FormLabel>2位</FormLabel>

                  </FormControl>
                  <FormControl>
                    <FormLabel>3位</FormLabel>

                  </FormControl>
                </>
              )}
            </Stack>
          )}
        </ModalBody>
      </ModalContent>
    </Modal>
  );
});
