/* eslint-disable react-hooks/exhaustive-deps */
import axios from "axios";
import { useCallback, useState } from "react";

import { Participant } from "../types/api/Participant";
import { useMessage } from "./useMessage";
import { useAllClass } from "../hooks/useAllClass";

export const useAllParticipant = () => {
  const { showMessage } = useMessage();
  const [loading2, setLoading2] = useState(false);
  const [users1, setUsers1] = useState<Array<Participant>>([]);
  const [users2, setUsers2] = useState<Array<Participant>>([]);
  const { getClases, clases } = useAllClass();

  const getUsers = useCallback((tournamentId: number) => {
    setLoading2(true);
    const stringClasses = clases.map(participant => participant.className);
    console.log("あ"+className1);

    axios
    .get<{ [key: string]: Participant[] }>(`http://localhost:8080/apiTournament/getParticipant?tournamentId=${tournamentId}&className1=${stringClasses[0]}&className2=${stringClasses[1]}`)
      .then((res) => {
        console.log("API Response:", res.data);
        const usersArray1 = res.data[className1] || [];
        const usersArray2 = res.data[className2] || [];
        console.log("Users Array 1:", usersArray1);
        console.log("Users Array 2:", usersArray2);

        setUsers1(usersArray1);
        setUsers2(usersArray2);
      })
      .catch(() => {
        showMessage({ title: "参加者取得に失敗しました", status: "error" });
      })
      .finally(() => {
        setLoading2(false);
      });
  }, []);
  return { getUsers, loading2, users1, users2 };
};
