/* eslint-disable react-hooks/exhaustive-deps */
import axios from "axios";
import { useCallback, useState } from "react";

import { Participant } from "../types/api/Participant";
import { useMessage } from "./useMessage";

export const useAllClass = () => {
  const { showMessage } = useMessage();
  const [clases, setClases] = useState<Array<Participant>>([]);

  const getClases = useCallback((tournamentId: number) => {
    axios
      .get<Array<Participant>>(`http://localhost:8080/apiTournament/getClass?tournamentId=${tournamentId}`)
      .then((res) => setClases(res.data))
      .then()
      .catch(() => {
        showMessage({ title: "取得に失敗しました", status: "error" });
      })
      .finally(() => {

      });
  }, []);
  return { getClases,  clases };
};
