import {
  Stack,
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalCloseButton,
  ModalBody,
  FormLabel,
  FormControl,
  Input,
  ModalFooter,
  Select,
} from "@chakra-ui/react";
import React, { ChangeEvent, useEffect, useState } from "react";
import { memo } from "react";

import { PrimaryButton } from "../../atoms/button/PrimaryButton";

type Props = {
  isOpen: boolean;
  onClose: () => void;
  options: string[];
};

export const TournamentDetailModal = memo((props: Props) => {
  const { isOpen, onClose, options } = props;

  const [number1, setNumber1] = useState("");
  const [number2, setNumber2] = useState("");


  const onChangeNumber1 = (e: React.ChangeEvent<HTMLSelectElement>) => setNumber1(e.target.value);
  const onChangeNumber2 = (e: ChangeEvent<HTMLInputElement>) =>
    setNumber2(e.target.value);

  const onClickUpdate = () => alert();

  return (
    <Modal
      isOpen={isOpen}
      onClose={onClose}
      autoFocus={false}
      motionPreset="slideInBottom"
    >
      <ModalOverlay />
      <ModalContent pb={2}>
        <ModalHeader>予想</ModalHeader>
        <ModalCloseButton />
        <ModalBody mx={4}>
          <Stack spacing={4}>
          <FormControl>
              <FormLabel>1位</FormLabel>
              <Select value={number1} onChange={(e) => onChangeNumber1(e.target.value)}>
                <option value="" disabled>Select 1st place</option>
                {options.map((option) => (
                  <option key={option} value={option}>
                    {option}
                  </option>
                ))}
              </Select>
            </FormControl>
            <FormControl>
              <FormLabel>2位</FormLabel>
              <Input
                value={number2}
                onChange={onChangeNumber2}
              />
            </FormControl>
          </Stack>
        </ModalBody>
          <ModalFooter>
            <PrimaryButton onClick={onClickUpdate}>登録</PrimaryButton>
          </ModalFooter>
      </ModalContent>
    </Modal>
  );
});
