import {
  Stack,
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalCloseButton,
  ModalBody,
  FormLabel,
  FormControl,
  Input,
  ModalFooter,
} from "@chakra-ui/react";
import React, { ChangeEvent, useEffect, useState } from "react";
import { memo } from "react";

import { tournament } from "../../../types/api/tournament";
import { PrimaryButton } from "../../atoms/button/PrimaryButton";

type Props = {
  isOpen: boolean;
  onClose: () => void;
};

export const TournamentDetailModal = memo((props: Props) => {
  const { isOpen, onClose } = props;

  const [tournamentName, setTournamentname] = useState("");
  const [name, setName] = useState("");


  const onChangeTournamentName = (e: ChangeEvent<HTMLInputElement>) =>
    setTournamentname(e.target.value);
  const onChangeName = (e: ChangeEvent<HTMLInputElement>) =>
    setName(e.target.value);

  const onClickUpdate = () => alert();

  return (
    <Modal
      isOpen={isOpen}
      onClose={onClose}
      autoFocus={false}
      motionPreset="slideInBottom"
    >
      <ModalOverlay />
      <ModalContent pb={2}>
        <ModalHeader>予想</ModalHeader>
        <ModalCloseButton />
        <ModalBody mx={4}>
          <Stack spacing={4}>
            <FormControl>
              <FormLabel>クラス</FormLabel>
              <Input
                value={tournamentName}
                onChange={onChangeTournamentName}  
              />
            </FormControl>
            <FormControl>
              <FormLabel>フルネーム</FormLabel>
              <Input
                value={name}
                onChange={onChangeName}
              />
            </FormControl>
          </Stack>
        </ModalBody>
          <ModalFooter>
            <PrimaryButton onClick={onClickUpdate}>登録</PrimaryButton>
          </ModalFooter>
      </ModalContent>
    </Modal>
  );
});
