import {
  Stack,
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalCloseButton,
  ModalBody,
  FormLabel,
  FormControl,
  Input,
  ModalFooter,
  Select,
} from "@chakra-ui/react";
import React, { ChangeEvent, useEffect, useState } from "react";
import { memo } from "react";

import { PrimaryButton } from "../../atoms/button/PrimaryButton";
import { tournament } from "../../../types/api/tournament";
import { Participant } from "../../../types/api/Participant";

type Props = {
  isOpen: boolean;
  onClose: () => void;
  users1: Array<Participant>;
  users2: Array<Participant>;
  clases: Array<Participant>;
  selectedTournament: tournament | null;
};

export const TournamentDetailModal = memo((props: Props) => {
  const { isOpen, onClose, users1, users2, clases, selectedTournament } = props;

  const [number1, setNumber1] = useState("");
  const [number2, setNumber2] = useState("");
  const [className1, className2] = clases;


  const onChangeNumber1 = (e: React.ChangeEvent<HTMLSelectElement>) =>
    setNumber1(e.target.value);
  const onChangeNumber2 = (e: ChangeEvent<HTMLInputElement>) =>
    setNumber2(e.target.value);

  const onClickUpdate = () => alert();

  return (
    <Modal
      isOpen={isOpen}
      onClose={onClose}
      autoFocus={false}
      motionPreset="slideInBottom"
    >
      <ModalOverlay />
      <ModalContent pb={2}>
        <ModalHeader>予想</ModalHeader>
        <ModalCloseButton />
        <ModalBody mx={4}>
          <Stack spacing={4}>
            <FormControl>

              <FormLabel>1位</FormLabel>
              <Select value={number1} onChange={onChangeNumber1} defaultValue="">
                <option value="" disabled>Select 1st place</option>
                {users1.map((user, participantId) => (
                  <option key={participantId} value={user.participantName}>
                    {user.participantName} - {user.affiliation}
                  </option>
                ))}
              </Select>
            </FormControl>
            <FormControl>
              <FormLabel>2位</FormLabel>
              <Input
                value={number2}
                onChange={onChangeNumber2}
              />
            </FormControl>
          </Stack>
        </ModalBody>
        <ModalFooter>
          <PrimaryButton onClick={onClickUpdate}>登録</PrimaryButton>
        </ModalFooter>
      </ModalContent>
    </Modal>
  );
});
